UPDATE "Kupovina" SET Cena=25000 WHERE Kupovina.IdA=(SELECT Automobil.id FROM "Kupovina", "Automobil", "Model" WHERE Kupovina.IdA=Automobil.id AND Automobil.IdM=Model.id AND Model.Model="A4") AND Kupovina.IdK=(SELECT Kupac.id FROM "Kupovina", "Kupac" WHERE Kupac.ime="Zoran")
UPDATE "Nabavka" SET Cena= (Nabavka.cena*1.10) WHERE Nabavka.IdA<= (SELECT Automobil.id FROM Automobil, Model WHERE Automobil.IdM=Model.id AND Model.Marka="Audi" ORDER BY Automobil.id DESC LIMIT 1) AND Nabavka.IdA>= (SELECT Automobil.id FROM Automobil, Model WHERE Automobil.IdM=Model.id AND Model.Marka="Audi" ORDER BY Automobil.id ASC LIMIT 1)
